-- DROP TABLE nobels, categories;


CREATE TABLE categories (
    category VARCHAR(128) NOT NULL,
    PRIMARY KEY (category)
);





CREATE TABLE nobels (
    id SERIAL,
    year INT NOT NULL,
    category VARCHAR(128) NOT NULL,
    name VARCHAR(128) NOT NULL,
    birthdate INT,
    birthplace VARCHAR(64),
    county VARCHAR(128),
    motivation TEXT,
    PRIMARY KEY (id)
);

ALTER TABLE nobels ADD FOREIGN KEY (category) REFERENCES categories(category);




