INSERT INTO nobels VALUES (DEFAULT,1906,'physics','Joseph John Thomson',1856,'Cheetham Hill near Manchester','United Kingdom','The Nobel Prize in Physics 1906 was awarded to J.J. Thomson in recognition of the great merits of his theoretical and experimental investigations on the conduction of electricity by gases.');
