INSERT INTO nobels VALUES (DEFAULT,1907,'literature','Rudyard Kipling',1865,'Bombay','British India','The Nobel Prize in Literature 1907 was awarded to Rudyard Kipling in consideration of the power of observation, originality of imagination, virility of ideas and remarkable talent for narration which characterize the creations of this world-famous author.');
INSERT INTO nobels VALUES (DEFAULT,1907,'medicine','Charles Louis Alphonse Laveran',1845,'Paris','France','The Nobel Prize in Physiology or Medicine 1907 was awarded to Alphonse Laveran in recognition of his work on the role played by protozoa in causing diseases.');
