INSERT INTO nobels VALUES (DEFAULT,1909,'chemistry','Wilhelm Ostwald',1853,'Riga Latvia','Russian Empire','The Nobel Prize in Chemistry 1909 was awarded to Wilhelm Ostwald in recognition of his work on catalysis and for his investigations into the fundamental principles governing chemical equilibria and rates of reaction.');
INSERT INTO nobels VALUES (DEFAULT,1909,'literature','Selma Ottilia Lovisa Lagerlöf',1858,'Mårbacka','Sweden','The Nobel Prize in Literature 1909 was awarded to Selma Lagerlöf in appreciation of the lofty idealism, vivid imagination and spiritual perception that characterize her writings.');
