INSERT INTO nobels VALUES (DEFAULT,1909,'physics','Guglielmo Marconi',1874,'Bologna','Italy','The Nobel Prize in Physics 1909 was awarded jointly to Guglielmo Marconi and Karl Ferdinand Braun in recognition of their contributions to the development of wireless telegraphy');
