INSERT INTO nobels VALUES (DEFAULT,1915,'chemistry','Richard Martin Willstätter',1872,'Karlsruhe','Germany','The Nobel Prize in Chemistry 1915 was awarded to Richard Willstätter for his researches on plant pigments, especially chlorophyll.');
INSERT INTO nobels VALUES (DEFAULT,1915,'literature','Romain Rolland',1866,'Clamecy','France','The Nobel Prize in Literature 1915 was awarded to Romain Rolland as a tribute to the lofty idealism of his literary production and to the sympathy and love of truth with which he has described different types of human beings.');
INSERT INTO nobels VALUES (DEFAULT,1915,'physics','Sir William Henry Bragg',1862,'Wigton','United Kingdom','The Nobel Prize in Physics 1915 was awarded jointly to Sir William Henry Bragg and William Lawrence Bragg for their services in the analysis of crystal structure by means of X-rays');
