INSERT INTO nobels VALUES (DEFAULT,1916,'literature','Carl Gustaf Verner von Heidenstam',1859,'Olshammar','Sweden','The Nobel Prize in Literature 1916 was awarded to Verner von Heidenstam in recognition of his significance as the leading representative of a new era in our literature.');
INSERT INTO nobels VALUES (DEFAULT,1917,'literature','Henrik Pontoppidan',1857,'Fredericia','Denmark','The Nobel Prize in Literature 1917 was divided equally between Karl Adolph Gjellerup for his varied and rich poetry, which is inspired by lofty ideals and Henrik Pontoppidan for his authentic descriptions of present-day life in Denmark.');
INSERT INTO nobels VALUES (DEFAULT,1917,'literature','Karl Adolph Gjellerup',1857,'Roholte','Denmark','The Nobel Prize in Literature 1917 was divided equally between Karl Adolph Gjellerup for his varied and rich poetry, which is inspired by lofty ideals and Henrik Pontoppidan for his authentic descriptions of present-day life in Denmark.');
