INSERT INTO nobels VALUES (DEFAULT,1918,'physics','Max Karl Ernst Ludwig Planck',1858,'Kiel','Schleswig (now Germany)','The Nobel Prize in Physics 1918 was awarded to Max Planck in recognition of the services he rendered to the advancement of Physics by his discovery of energy quanta.');
