INSERT INTO nobels VALUES (DEFAULT,1921,'chemistry','Frederick Soddy',1877,'Eastbourne','United Kingdom','The Nobel Prize in Chemistry 1921 was awarded to Frederick Soddy for his contributions to our knowledge of the chemistry of radioactive substances, and his investigations into the origin and nature of isotopes.');
INSERT INTO nobels VALUES (DEFAULT,1921,'literature','Anatole France',1844,'Paris','France','The Nobel Prize in Literature 1921 was awarded to Anatole France in recognition of his brilliant literary achievements, characterized as they are by a nobility of style, a profound human sympathy, grace, and a true Gallic temperament.');
