INSERT INTO nobels VALUES (DEFAULT,1922,'literature','Jacinto Benavente',1866,'Madrid','Spain','The Nobel Prize in Literature 1922 was awarded to Jacinto Benavente for the happy manner in which he has continued the illustrious traditions of the Spanish drama.');
INSERT INTO nobels VALUES (DEFAULT,1922,'medicine','Archibald Vivian Hill',1886,'Bristol','United Kingdom','The Nobel Prize in Physiology or Medicine 1922 was divided equally between Archibald Vivian Hill for his discovery relating to the production of heat in the muscle and Otto Fritz Meyerhof for his discovery of the fixed relationship between the consumption of oxygen and the metabolism of lactic acid in the muscle.');
INSERT INTO nobels VALUES (DEFAULT,1922,'medicine','Otto Fritz Meyerhof',1884,'Hanover','Germany','The Nobel Prize in Physiology or Medicine 1922 was divided equally between Archibald Vivian Hill for his discovery relating to the production of heat in the muscle and Otto Fritz Meyerhof for his discovery of the fixed relationship between the consumption of oxygen and the metabolism of lactic acid in the muscle.');
